function wordsearch(){
    var word = document.getElementById('wordToBeRmvd').value
    if (wordbank == undefined){
        wordbank = [];
    }
    if(words == undefined){
        words = [];
    }
    if(words.includes(word)){
        words.splice(words.indexOf(word), 1)
        wordbank.splice(words.indexOf(word), 1)
        alert("Word has been removed")
    }else{
        alert("Word not found")
        }

    chrome.storage.sync.set({'wordobjects': wordbank}, function(){
        console.log("success")
    })
    chrome.storage.sync.set({'words': words}, function(){
        console.log('success')
    })
    
}



